options.css:
.evan---badge {

}



/* -------------------------------- 

Icons 

-------------------------------- */

.cd-icon {
  --size: 1em;
  font-size: var(--size);
  height: 1em;
  width: 1em;
  display: inline-block;
  color: inherit;
  fill: currentColor;
  line-height: 1;
  flex-shrink: 0;
  max-width: initial;
}

.cd-icon use { /* SVG symbols - enable icon color corrections */
  color: inherit;
  fill: currentColor;
}

/* -------------------------------- 

Component 

-------------------------------- */

.badges {
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
}

.badge {
  /* reset - in case the class is applied to a <button> */
  border: 0;
  color: inherit;
  line-height: 1;
  appearance: none;

  display: inline-flex;
  align-items: center;
  border-radius: 0.375em;

  background-color: hsl(240, 4%, 90%);
  padding: 0.5rem 1rem;

  font-size: 0.9375rem;
}

.badge--outline {
  background-color: transparent;
  box-shadow: inset 0 0 0 1px hsl(240, 4%, 90%);
}

.badge--contrast-higher {
  background-color: hsl(230, 13%, 9%);
  color: hsl(0, 0%, 100%);
}

.badge--primary {
  background-color: hsl(250, 84%, 54%);
  color: hsl(0, 0%, 100%);
}

.badge--primary-light {
  background-color: hsla(250, 84%, 54%, 0.2);
  color: hsl(230, 13%, 9%);
}

.badge--accent {
  background-color: hsl(342, 89%, 48%);
  color: hsl(0, 0%, 100%);
}

.badge--accent-light {
  background-color: hsla(342, 89%, 48%, 0.2);
  color: hsl(230, 13%, 9%);
}

.badge--error {
  background-color: hsl(342, 89%, 48%);
  color: hsl(0, 0%, 100%);
}

.badge--error-light {
  background-color: hsla(342, 89%, 48%, 0.2);
  color: hsl(230, 13%, 9%);
}

.badge--success {
  background-color: hsl(170, 78%, 36%);
  color: hsl(0, 0%, 100%);
}

.badge--success-light {
  background-color: hsla(170, 78%, 36%, 0.2);
  color: hsl(230, 13%, 9%);
}

.badge--warning {
  background-color: hsl(35, 79%, 66%);
  color: hsl(0, 0%, 100%);
}

.badge--warning-light {
  background-color: hsla(35, 79%, 66%, 0.2);
  color: hsl(230, 13%, 9%);
}

.badge--icon {
  gap: 0.5rem;
}

