/* -------------------------------- 

File#: _1_card-v14
Title: Card v14
Descr: Container of information used as teaser for further content exploration
Usage: codyhouse.co/license

-------------------------------- */


/* -------------------------------- 

Icons 

-------------------------------- */

.cd-icon {
  --size: 1em;
  font-size: var(--size);
  height: 1em;
  width: 1em;
  display: inline-block;
  color: inherit;
  fill: currentColor;
  line-height: 1;
  flex-shrink: 0;
  max-width: initial;
}

.cd-icon use { /* SVG symbols - enable icon color corrections */
  color: inherit;
  fill: currentColor;
}

/* -------------------------------- 

Component 

-------------------------------- */

.card-v14 {
  display: flex;
  flex-direction: column;

  background-color: hsl(0, 0%, 100%);
  box-shadow: 0 0 0 1px hsla(230, 13%, 9%, 0.05),
              0 0.3px 0.4px hsla(230, 13%, 9%, 0.02),
              0 0.9px 1.5px hsla(230, 13%, 9%, 0.045),
              0 3.5px 6px hsla(230, 13%, 9%, 0.09);
  border-radius: 0.375em;
  padding: 1rem;

  color: inherit;
  text-decoration: none;

  transition: 0.3s;
}

.card-v14:hover {
  box-shadow: 0 0 0 1px hsla(230, 13%, 9%, 0.05),
              0 0.9px 1.25px hsla(230, 13%, 9%, 0.025),
              0 3px 5px hsla(230, 13%, 9%, 0.05),
              0 12px 20px hsla(230, 13%, 9%, 0.09);
}

.card-v14__icon-wrapper {
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 1rem;

  width: 50px;
  height: 50px;
  border-radius: 50%;
  background-color: hsla(342, 89%, 48%, 0.2);
}

.card-v14__icon {
  --size: 24px;
  color: hsl(342, 89%, 48%);
}

.card-v14__description,
.card-v14__link {
  font-size: 0.9375rem;
}

.card-v14__description {
  color: hsl(225, 4%, 47%);
  line-height: 1.58;
  margin: 0.75rem 0 1rem;
}

.card-v14__link {
  text-align: right;
  margin-top: auto;
  color: hsl(250, 84%, 54%);
}

/* -------------------------------- 

Demo stuff 

-------------------------------- */

.demo-card-v14-grid {
  display: grid;
  gap: 1.5rem;
}

.demo-card-v14-grid > * {
  min-width: 0;
}

@media (min-width: 42.5rem) {
  .demo-card-v14-grid {
    grid-template-columns: repeat(2, 1fr);
  }
}

@media (min-width: 85.5rem) {
  .demo-card-v14-grid {
    grid-template-columns: repeat(3, 1fr);
  }
}

